html{ font-family: Arial, sans-serif; }
body{ margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; }
article, aside, header, main, nav{ display: block; }
a{ background-color: transparent; }
 strong{ font-weight: bold; }
h1{ font-size: 2em; margin-top: 0.67em; margin-right: 0px; margin-bottom: 0.67em; margin-left: 0px; }
img{ border-top-width: 0px; border-right-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-style: initial; border-color: initial; }
button, input, select{ color: inherit; font: inherit; margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; }
button{ overflow-x: visible; overflow-y: visible; }
button, select{ text-transform: none; }
button, input[type="submit"]{ -webkit-appearance: button; cursor: pointer; }
input{ line-height: normal; }
input[type="search"]{ -webkit-appearance: textfield; box-sizing: content-box; }
body, button, input, select{ color: rgb(17, 17, 17); font-family: Arial, sans-serif; line-height: 1.6; font-size: 0.9375rem; }
h1, h2{ clear: both; }
p{ margin-top: 0px; margin-bottom: 1.5rem; }
html{ box-sizing: border-box; }
*{ box-sizing: inherit; }
body{ background-image: initial; background-attachment: initial; background-origin: initial; background-clip: initial; background-color: rgb(250, 250, 250); background-position: initial initial; background-repeat: initial initial; }
ul{ margin-top: 0px; margin-right: 0px; margin-left: 0px; margin-bottom: 1.5rem; padding-left: 1.5rem; }
ul{ list-style-type: disc; list-style-position: initial; list-style-image: initial; }
img{ height: auto; max-width: 100%; }
button, input[type="submit"]{ border-top-width: 1px; border-right-width: 1px; border-bottom-width: 1px; border-left-width: 1px; border-top-style: solid; border-right-style: solid; border-bottom-style: solid; border-left-style: solid; border-color: initial; border-top-color: rgb(204, 204, 204); border-right-color: rgb(204, 204, 204); border-bottom-color: rgb(187, 187, 187); border-left-color: rgb(204, 204, 204); border-top-left-radius: 3px 3px; border-top-right-radius: 3px 3px; border-bottom-right-radius: 3px 3px; border-bottom-left-radius: 3px 3px; background-image: initial; background-attachment: initial; background-origin: initial; background-clip: initial; background-color: rgb(230, 230, 230); box-shadow: rgba(255, 255, 255, 0.496094) 0px 1px 0px inset, rgba(255, 255, 255, 0.496094) 0px 15px 17px inset, rgba(0, 0, 0, 0.046875) 0px -5px 12px inset; color: rgba(0, 0, 0, 0.796875); font-size: 0.75rem; line-height: 1; padding-top: 1rem; padding-right: 1rem; padding-bottom: 1rem; padding-left: 1rem; text-shadow: rgba(255, 255, 255, 0.796875) 0px 1px 0px; background-position: initial initial; background-repeat: initial initial; }
 input[type="search"]{ color: rgb(102, 102, 102); border-top-width: 1px; border-right-width: 1px; border-bottom-width: 1px; border-left-width: 1px; border-top-style: solid; border-right-style: solid; border-bottom-style: solid; border-left-style: solid; border-top-color: rgb(204, 204, 204); border-right-color: rgb(204, 204, 204); border-bottom-color: rgb(204, 204, 204); border-left-color: rgb(204, 204, 204); border-top-left-radius: 3px 3px; border-top-right-radius: 3px 3px; border-bottom-right-radius: 3px 3px; border-bottom-left-radius: 3px 3px; }
 input[type="search"]{ padding-top: 3px; padding-right: 3px; padding-bottom: 3px; padding-left: 3px; }
a{ color: rgb(0, 2, 86); text-decoration: none; }
.main-navigation{ clear: both; display: block; float: left; width: 100%; }
.main-navigation ul{ display: none; list-style-type: none; list-style-position: initial; list-style-image: initial; margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding-left: 0px; }
.main-navigation li{ float: left; position: relative; }
.main-navigation a{ display: block; text-decoration: none; }
.menu-toggle{ display: block; }
@media screen and (min-width: 37.5em){
.menu-toggle{ display: none; }
.main-navigation ul{ display: block; }
}
.screen-reader-text{ clip: rect(1px 1px 1px 1px); position: absolute !important; height: 1px; width: 1px; overflow-x: hidden; overflow-y: hidden; }
.alignleft{ display: inline; float: left; margin-right: 1.25rem; }
 .entry-content::before, .entry-content::after, .site-header::before, .site-header::after, .site-content::before, .site-content::after{ content: ''; display: table; }
 .entry-content::after, .site-header::after, .site-content::after{ clear: both; }
.widget{ margin-top: 0px; margin-right: 0px; margin-bottom: 1.5em; margin-left: 0px; }
.widget select{ max-width: 100%; }
.hentry{ margin-top: 0px; margin-right: 0px; margin-bottom: 1.5em; margin-left: 0px; }
 .entry-content{ margin-top: 1.5em; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; }
.site{ background-color: rgb(255, 255, 255); border-top-width: 1px; border-right-width: 1px; border-bottom-width: 1px; border-left-width: 1px; border-top-style: solid; border-right-style: solid; border-bottom-style: solid; border-left-style: solid; border-top-color: rgb(232, 232, 232); border-right-color: rgb(232, 232, 232); border-bottom-color: rgb(232, 232, 232); border-left-color: rgb(232, 232, 232); margin-right: auto; margin-left: auto; margin-top: 1.25rem; margin-bottom: 1.25rem; padding-top: 1.25rem; padding-right: 1.25rem; padding-bottom: 1.25rem; padding-left: 1.25rem; max-width: 58.75rem; }
@media only screen and (min-width: 940px){
.site{ box-shadow: rgb(221, 221, 221) 0px 5px 10px; }
}
@media only screen and (min-width: 940px){
.content-area{ float: left; max-width: 37.5rem; }
}
@media only screen and (min-width: 940px){
.widget-area{ float: right; max-width: 17.5rem; }
}